    	viewer.addValue(statistic.kundenGlobal.anrufeVerweilzeitSum/Math.max(1,statistic.kundenGlobal.anrufeErfolg),"Verweilzeit-alle",language.tr("SimStatistic.AllClients"));
    	viewer.setSeriesPaint(0,"blue");
